#include <stdint.h>



uint32_t OnesComplementSum(const uint8_t* inData, int len, uint32_t sum)
{
    const uint16_t * b16 = reinterpret_cast<const uint16_t *>(inData);

    uint32_t a = 0, b = 0, c = 0, d = 0;

   


#ifdef UGLY_LOOP
 auto run8 = [&]{
        a += b16[0];
        b += b16[1];
        c += b16[2];
        d += b16[3];
        b16 += 4;
    };
    while (len > 32)
    {
        run8();
        run8();
        run8();
        run8();
        len -= 32;
    }
    
    while (len > 8)
    {
        run8();
        len -= 8;
    }
#elif CLEAN_LOOP
    const int quick_len=len/8;
    const uint16_t * const the_end=b16+quick_len/2;
    len -= quick_len*8;
    for (; b16 < the_end; b16+=4)
    {
        a += b16[0];
        b += b16[1];
        c += b16[2];
        d += b16[3];
    }
  
#else
#error WHICH LOOP?
#endif

    sum += (a + b) + (c + d);

    auto reduce = [&] {
        sum = (sum & uint16_t(-1)) + (sum >> 16);
        sum > uint16_t(-1) ? sum -= uint16_t(-1) : sum;
    };

    reduce();

    while ((len -= 2) >= 0) sum += *b16++;

    if (len == -1)
    {
        // Add the last byte
        sum += static_cast<uint16_t>(reinterpret_cast<const uint8_t *>(b16)[0]);
    }

    reduce();

    return sum;
}


