#include <iostream>#include <iterator>#include <algorithm>#include <map>#include <fstream>#include <string>std::string loadText(char* filename){    std::ifstream ifs(filename,std::ios::in);    if(!ifs.good())        return std::string();    ifs.seekg(-1,std::ios::end);                       //1.to aviod '\0'    std::streamsize size(ifs.tellg());    ifs.seekg(0,std::ios::beg);    std::string buffer(size,0);    ifs.read((char*)buffer.data(),size);    std::cout<<(void*)buffer.data()<<std::endl;                 //2.print the pointer of buffer    return buffer;}int main(){    std::string str(loadText("//Archive2//6a//5e2587570b337e//main.cpp"));    std::cout<<(void*)str.data()<<std::endl;                    //3.print the pointer of str    system("pause");    std::cout<<">"<<str<<"<";    system("pause");    return 0;}